import { useState } from "react";
import './tela.css'

function Cartao() {
  const [formData, setFormData] = useState({
    name: "",
    cardNumber: "",
    expiryDate: "",
    cvc: "",
  });

  const [cardData, setCardData] = useState({
    name: "",
    cardNumber: "",
    expiryDate: "",
    cvc: "",
  });

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const { name, cardNumber, expiryDate, cvc } = formData;
    const cardNumberPattern = /^\d{16}$/;
    const expiryDatePattern = /^\d{2}\/\d{2}$/;
    const cvcPattern = /^\d{3}$/;
    if (name === "") {
      alert("Por favor, preencha o nome.");
    } else if (cardNumber === "" || !cardNumberPattern.test(cardNumber)) {
      alert("Por favor, preencha o número do cartão corretamente (16 dígitos).");
    } else if (expiryDate === "" || !expiryDatePattern.test(expiryDate)) {
      alert("Por favor, preencha a data de validade corretamente (MM/AA).");
    } else if (cvc === "" || !cvcPattern.test(cvc)) {
      alert("Por favor, preencha o código de segurança corretamente (3 dígitos).");
    } else {
      setCardData({ name, cardNumber, expiryDate, cvc });
    }
  };

  return (
    <div className="">
      <h1>Dados do cartão:</h1>
      <p>Nome: {cardData.name}</p>
      <p>Número do cartão: {cardData.cardNumber}</p>
      <p>Data de validade: {cardData.expiryDate}</p>
      <p>CVC: {cardData.cvc}</p>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Nome:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={formData.name}
          onChange={handleFormChange}
        />
        <label htmlFor="cardNumber">Número do cartão:</label>
        <input
          type="text"
          id="cardNumber"
          name="cardNumber"
          value={formData.cardNumber}
          onChange={handleFormChange}
        />
        <label htmlFor="expiryDate">Data de validade:</label>
        <input
          type="text"
          id="expiryDate"
          name="expiryDate"
          value={formData.expiryDate}
          onChange={handleFormChange}
        />
        <label htmlFor="cvc">CVC:</label>
        <input
          type="text"
          id="cvc"
          name="cvc"
          value={formData.cvc}
          onChange={handleFormChange}
        />
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
}

export default Cartao;
