import { useState } from "react";
import './tela.css'

function Cartao() {
  const [formData, setFormData] = useState({
    name: "",
    cardNumber: "",
    expiryDate1: "",
    expiryDate2: "",
    cvc: "",
  });

  const [cardData, setCardData] = useState({
    name: "Pedro H. Araújo",
    cardNumber: "0000 0000 0000 0000",
    expiryDate1: "00",
    expiryDate2: "00",
    cvc: "000",
  });

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const { name, cardNumber, expiryDate1, expiryDate2, cvc } = formData;
    const cardNumberPattern = /^\d{4} \d{4} \d{4} \d{4}$/;
    const expiryDate1Pattern = /^\d{2}$/;
    const expiryDate2Pattern = /^\d{2}$/;
    const cvcPattern = /^\d{3}$/;
    if (name === "") {
      alert("Por favor, preencha o nome.");
    } else if (cardNumber === "" || !cardNumberPattern.test(cardNumber)) {
      alert("Por favor, preencha o número do cartão corretamente (16 dígitos).");
    } else if (expiryDate1 === "" || !expiryDate1Pattern.test(expiryDate1)) {
      alert("Por favor, preencha a data de validade corretamente (MM/AA).");
    } else if (expiryDate2 === "" || !expiryDate2Pattern.test(expiryDate2)) {
        alert("Por favor, preencha a data de validade corretamente (MM/AA).");
    } else if (cvc === "" || !cvcPattern.test(cvc)) {
      alert("Por favor, preencha o código de segurança corretamente (3 dígitos).");
    } else {
      setCardData({ name, cardNumber, expiryDate1, expiryDate2, cvc });
      // <img src="imagens/bg-card-back.png" alt="imagem-back" className="aa"/>
    }
  };

  return (
    <div className="fundo-cartao">

      <div className="dados-cartao">
        <h1>Dados do cartão:</h1>
        <p>{cardData.name}</p>
        <p>{cardData.cardNumber}</p>
        <p>{cardData.expiryDate1}/{cardData.expiryDate2}</p>
        <div className="cvc">
          <p>{cardData.cvc}</p>
        </div>
      </div>
      <section className="form">

        <form onSubmit={handleSubmit}>

          <label htmlFor="name">Nome:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleFormChange}
          />

          <label htmlFor="cardNumber">Número do cartão:</label>
          <input
            type="text"
            id="cardNumber"
            name="cardNumber"
            value={formData.cardNumber}
            onChange={handleFormChange}
          />

          <label htmlFor="expiryDate1">EXP.DATE(MM/YY)</label>
          <input
            type="text"
            id="expiryDate1"
            name="expiryDate1"
            value={formData.expiryDate1}
            onChange={handleFormChange}
          />

          <label htmlFor="expiryDate2">EXP.DATE(MM/YY)</label>
          <input
            type="text"
            id="expiryDate2"
            name="expiryDate2"
            value={formData.expiryDate2}
            onChange={handleFormChange}
          />  

          <label htmlFor="cvc">CVC:</label>
          <input
            type="text"
            id="cvc"
            name="cvc"
            value={formData.cvc}
            onChange={handleFormChange}
          />

          <button type="submit">Enviar</button>
        </form>
      </section>
    </div>
  );
}

export default Cartao;
