import './tela.css';
import { useState } from "react";

function Cartao() {
    const [formData, setFormData] = useState({
      name: "",
      cardNumber: "",
      expiryDate: "",
      cvc: "",
    });
  
    const [cardData, setCardData] = useState({
      name: "",
      cardNumber: "",
      expiryDate: "",
      cvc: "",
    });
  
    const [formErrors, setFormErrors] = useState({
      name: "",
      cardNumber: "",
      expiryDate: "",
      cvc: "",
    });
  
    const handleFormChange = (event) => {
      const { name, value } = event.target;
      let error = "";
  
      switch (name) {
        case "name":
          error = value.trim() === "" ? "Por favor, informe um nome válido." : "";
          break;
        case "cardNumber":
          const cardNumberPattern = /^\d{16}$/;
          error = !cardNumberPattern.test(value) ? "Por favor, informe um número de cartão válido." : "";
          break;
        case "expiryDate":
          const expiryDatePattern = /^\d{2}\/\d{2}$/;
          error = !expiryDatePattern.test(value) ? "Por favor, informe uma data de validade válida." : "";
          break;
        case "cvc":
          const cvcPattern = /^\d{3}$/;
          error = !cvcPattern.test(value) ? "Por favor, informe um CVC válido." : "";
          break;
        default:
          break;
      }
  
      setFormErrors({ ...formErrors, [name]: error });
      setFormData({ ...formData, [name]: value });
    };
  
    const handleSubmit = (event) => {
      event.preventDefault();
      const { name, cardNumber, expiryDate, cvc } = formData;
      const cardNumberPattern = /^\d{16}$/;
      const expiryDatePattern = /^\d{2}\/\d{2}$/;
      const cvcPattern = /^\d{3}$/;
  
      const errors = {
        name: name.trim() === "" ? "Por favor, informe um nome válido." : "",
        cardNumber: !cardNumberPattern.test(cardNumber) ? "Por favor, informe um número de cartão válido." : "",
        expiryDate: !expiryDatePattern.test(expiryDate) ? "Por favor, informe uma data de validade válida." : "",
        cvc: !cvcPattern.test(cvc) ? "Por favor, informe um CVC válido." : "",
      };
  
      setFormErrors(errors);
  
      if (Object.values(errors).every((error) => error === "")) {
        setCardData({ name, cardNumber, expiryDate, cvc });
      }
    };
  

  return (
    <div>
      <h1>Dados do cartão:</h1>
      <p>Nome: {cardData.name}</p>
      <p>Número do cartão: {cardData.cardNumber}</p>
      <p>Data de validade: {cardData.expiryDate}</p>
      <p>CVC: {cardData.cvc}</p>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Nome:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={formData.name}
          onChange={handleFormChange}
        />
        <label htmlFor="cardNumber">Número do cartão:</label>
        <input
          type="text"
          id="cardNumber"
          name="cardNumber"
          value={formData.cardNumber}
          onChange={handleFormChange}
        />
        <label htmlFor="expiryDate">Data de validade:</label>
        <input
          type="text"
          id="expiryDate"
          name="expiryDate"
          value={formData.expiryDate}
          onChange={handleFormChange}
        />
        <label htmlFor="cvc">CVC:</label>
        <input
          type="text"
          id="cvc"
          name="cvc"
          value={formData.cvc}
          onChange={handleFormChange}
        />
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
}

export default Cartao;
