import { useState } from "react";
import './tela.css'

function Cartao() {
  const [formData, setFormData] = useState({
    name: "",
    cardNumber: "",
    expiryDate1: "",
    expiryDate2: "",
    cvc: "",
  });

  const [cardData, setCardData] = useState({
    name: "YOUR NAME",
    cardNumber: "0000 0000 0000 0000",
    expiryDate1: "00",
    expiryDate2: "00",
    cvc: "000",
  });

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const { name, cardNumber, expiryDate1, expiryDate2, cvc } = formData;
    const cardNumberPattern = /^\d{4} \d{4} \d{4} \d{4}$/;
    const expiryDate1Pattern = /^\d{2}$/;
    const expiryDate2Pattern = /^\d{2}$/;
    const cvcPattern = /^\d{3}$/;
    if (name === "") {
      alert("Por favor, preencha o nome.");
    } else if (cardNumber === "" || !cardNumberPattern.test(cardNumber)) {
      alert("Por favor, preencha o número do cartão corretamente (16 dígitos).");
    } else if (expiryDate1 === "" || !expiryDate1Pattern.test(expiryDate1)) {
      alert("Por favor, preencha a data de validade corretamente (MM/AA).");
    } else if (expiryDate2 === "" || !expiryDate2Pattern.test(expiryDate2)) {
        alert("Por favor, preencha a data de validade corretamente (MM/AA).");
    } else if (cvc === "" || !cvcPattern.test(cvc)) {
      alert("Por favor, preencha o código de segurança corretamente (3 dígitos).");
    } else {
      setCardData({ name, cardNumber, expiryDate1, expiryDate2, cvc });
      // <img src="imagens/bg-card-back.png" alt="imagem-back" className="aa"/>
    }
  };

  return (
    <div className="fundo-cartao">

      <div className="dados-cartao">
        <div className="bolas">
          <div className="bola branca"></div>
          <div className="bola cinza"></div>
        </div>
        <div className="separacao">
          <p>{cardData.cardNumber}</p>
          <p className="">{cardData.name} {cardData.expiryDate1}/{cardData.expiryDate2} </p>
        </div>
        <div className="cvc">
          <p>{cardData.cvc}</p>
        </div>
      </div>

      <section className="form">

        <form onSubmit={handleSubmit}>

          <label htmlFor="name">CARDHOLDER NAME</label>
          <input
            type="text"
            id="name"
            name="name"
            placeholder="jane"
            value={formData.name}
            onChange={handleFormChange}
          />

          <label htmlFor="cardNumber">CARD NUMBER</label>
          <input
            type="text"
            id="cardNumber"
            name="cardNumber"
            placeholder="0000 0000 0000 0000"
            value={formData.cardNumber}
            onChange={handleFormChange}
          />
          <p className="text">EXP.DATE(MM/YY) CVC</p>
          <div className="separador">
            <label htmlFor="expiryDate1"></label>
            <input
              className="inputs"
              type="text"
              id="expiryDate1"
              name="expiryDate1"
              placeholder="00"
              value={formData.expiryDate1}
              onChange={handleFormChange}
            />
            <label htmlFor="expiryDate2"></label>
            <input
              className="inputs"
              type="text"
              id="expiryDate2"
              name="expiryDate2"
              placeholder="00"
              value={formData.expiryDate2}
              onChange={handleFormChange}
            />
            <label htmlFor="cvc"></label>
            <input className="inputs teste"
              type="text"
              id="cvc"
              name="cvc"
              placeholder="000"
              value={formData.cvc}
              onChange={handleFormChange}
            />
          </div>

          <button type="submit" className="button1">Confirm</button>
        </form>
      </section>
    </div>
  );
}

export default Cartao;
